mapper.insert:
INSERT INTO item3
(
    item_name
    , price
    , description
)
VALUES
(
    #{itemName}
    , #{price}
    , #{description}
)

mapper.update:
UPDATE  item3
SET     item_name = #{itemName}
        , price = #{price}
        , description = #{description}
WHERE   item_id = #{itemId}

mapper.select:
SELECT  picture_url     AS pictureUrl
FROM    item3
WHERE   item_id = #{itemId}

mapper.insert:
