mapper.insert:
INSERT INTO item2
(
    item_name
    , price
    , description
    , picture_url
    , picture_url2
)
VALUES
(
    #{itemName}
    , #{price}
    , #{description}
    , #{pictureUrl}
    , #{pictureUrl2}
)

mapper.update:
UPDATE  item2
SET     item_name = #{itemName}
        , price = #{price}
        , description = #{description}
        , picture_url = #{pictureUrl}
        , picture_url2 = #{pictureUrl2}
WHERE   item_id = #{itemId}

mapper.select:
SELECT  picture_url AS pictureUrl
FROM    item2
WHERE   item_id = #{itemId}

SELECT  picture_url2    AS pictureUrl2
FROM    item2
WHERE   item_id = #{itemId}
